import React from 'react';
import { Drop, DropQuality, DROP_QUALITY_COLORS } from '../../types/drop';
import './DropCard.scss';
import Button from '../common/Button';

interface DropCardProps {
  drop: Drop;
  onUpgrade: (dropId: string) => void;
  onRelease: (dropId: string) => void;
  canUpgrade: boolean;
}

const DropCard: React.FC<DropCardProps> = ({ 
  drop, 
  onUpgrade, 
  onRelease,
  canUpgrade 
}) => {
  // Расчет времени с момента создания дропа (заглушка)
  const getTimeActive = (): string => {
    return "1д 2ч";
  };

  // Получение цвета в зависимости от качества дропа
  const qualityColor = DROP_QUALITY_COLORS[drop.quality];

  return (
    <div className={`drop-card ${drop.quality.toLowerCase()} ${!drop.isActive ? 'inactive' : ''}`}>
      <div className="drop-card__avatar" style={{borderColor: qualityColor}}>
        {drop.avatarUrl ? (
          <img src={drop.avatarUrl} alt="аватар" />
        ) : (
          <div className="drop-card__avatar-placeholder">
            {drop.name.charAt(0).toUpperCase()}
          </div>
        )}
        <div className="drop-card__level">Ур.{drop.level}</div>
      </div>

      <div className="drop-card__info">
        <div className="drop-card__header">
          <h3 className="drop-card__name">{drop.name}</h3>
          <span className="drop-card__quality" style={{backgroundColor: qualityColor}}>
            {drop.quality === DropQuality.COMMON && 'Обычный'}
            {drop.quality === DropQuality.RARE && 'Редкий'}
            {drop.quality === DropQuality.EPIC && 'Эпический'}
            {drop.quality === DropQuality.LEGENDARY && 'Легендарный'}
          </span>
        </div>
        
        <div className="drop-card__details">
          <div className="drop-card__detail">
            <span className="drop-card__detail-label">Доход:</span>
            <span className="drop-card__detail-value drop-card__income">
              {drop.income}/час
            </span>
          </div>
          
          <div className="drop-card__detail">
            <span className="drop-card__detail-label">Активен:</span>
            <span className="drop-card__detail-value">
              {getTimeActive()}
            </span>
          </div>
        </div>
      </div>
      
      <div className="drop-card__actions">
        <Button 
          variant="primary" 
          size="small" 
          onClick={() => onUpgrade(drop.id)}
          disabled={!canUpgrade}
        >
          Улучшить
        </Button>
        <Button 
          variant="outline" 
          size="small" 
          onClick={() => onRelease(drop.id)}
          className="drop-card__release-button"
        >
          Отпустить
        </Button>
      </div>
    </div>
  );
};

export default DropCard;
