import { Drop as NewDrop, DropQuality } from './drop';

// Расширяем типы для обеспечения совместимости
export type Drop = NewDrop;

// Экспортируем для обратной совместимости
export { DropQuality };

export interface DropUpgrade {
  cost: number;
  income_boost_percent: number;
}

// Мультипликаторы качества дропов
export const QUALITY_MULTIPLIERS = {
  common: 1.0,
  rare: 2.5, 
  epic: 6.0,
  legendary: 15.0
};

// Цвета для различных качеств дропов
export const QUALITY_COLORS = {
  common: '#78909C', // Серый
  rare: '#2196F3', // Синий
  epic: '#9C27B0', // Фиолетовый
  legendary: '#FFC107' // Золотой
};
