import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Drops.scss';
import DropCard from '../../components/Drops/DropCard';
import Button from '../../components/common/Button';
import Loader from '../../components/common/Loader';
import { useGame } from '../../context/GameContext';
import BottomNav from '../../components/common/BottomNav';

const DropsPage: React.FC = () => {
  const navigate = useNavigate();
  const { drops, setDrops, balance, lostDrops24h, totalDropsCount, dropsIncomePerHour, addDrop } = useGame();
  
  const [loading, setLoading] = useState<boolean>(true);
  const [selectedDrops, setSelectedDrops] = useState<string[]>([]);
  const [isMultiSelectMode, setIsMultiSelectMode] = useState<boolean>(false);

  // Имитация загрузки данных и добавление тестовых дропов если их нет
  useEffect(() => {
    // Добавляем тестовые дропы, если их нет
    if (drops.length === 0) {
      // Тестовые дропы
      const testDrops = [
        {
          id: '1',
          userId: '123456789',
          name: 'Алексей',
          avatar: 'https://randomuser.me/api/portraits/men/32.jpg',
          quality: 'common',
          level: 1,
          incomePerHour: 10,
          incomeMultiplier: 1.0,
          lastSeen: new Date(Date.now() - 1000 * 60 * 10).toISOString(), // 10 минут назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2).toISOString() // 2 дня назад
        },
        {
          id: '2',
          userId: '987654321',
          name: 'Мария',
          avatar: 'https://randomuser.me/api/portraits/women/43.jpg',
          quality: 'rare',
          level: 2,
          incomePerHour: 25,
          incomeMultiplier: 2.5,
          lastSeen: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString(), // 2 часа назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5).toISOString() // 5 дней назад
        },
        {
          id: '3',
          userId: '555555555',
          name: 'Иван',
          avatar: 'https://randomuser.me/api/portraits/men/67.jpg',
          quality: 'epic',
          level: 3,
          incomePerHour: 60,
          incomeMultiplier: 6.0,
          lastSeen: new Date().toISOString(), // сейчас
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 10).toISOString() // 10 дней назад
        },
        {
          id: '4',
          userId: '444444444',
          name: 'Екатерина',
          avatar: 'https://randomuser.me/api/portraits/women/22.jpg',
          quality: 'legendary',
          level: 4,
          incomePerHour: 150,
          incomeMultiplier: 15.0,
          lastSeen: new Date(Date.now() - 1000 * 60 * 60 * 30).toISOString(), // 30 часов назад (неактивный)
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 15).toISOString() // 15 дней назад
        },
        {
          id: '5',
          userId: '666666666',
          name: 'Антон',
          avatar: 'https://randomuser.me/api/portraits/men/91.jpg',
          quality: 'rare',
          level: 2,
          incomePerHour: 30,
          incomeMultiplier: 2.5,
          lastSeen: new Date(Date.now() - 1000 * 60 * 30).toISOString(), // 30 минут назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 3).toISOString() // 3 дня назад
        },
        {
          id: '6',
          userId: '777777777',
          name: 'Ольга',
          avatar: 'https://randomuser.me/api/portraits/women/65.jpg',
          quality: 'common',
          level: 1,
          incomePerHour: 15,
          incomeMultiplier: 1.0,
          lastSeen: new Date(Date.now() - 1000 * 60 * 120).toISOString(), // 2 часа назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 1).toISOString() // 1 день назад
        },
        {
          id: '7',
          userId: '888888888',
          name: 'Дмитрий',
          avatar: 'https://randomuser.me/api/portraits/men/45.jpg',
          quality: 'epic',
          level: 2,
          incomePerHour: 45,
          incomeMultiplier: 6.0,
          lastSeen: new Date(Date.now() - 1000 * 60 * 15).toISOString(), // 15 минут назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 7).toISOString() // 7 дней назад
        },
        {
          id: '8',
          userId: '999999999',
          name: 'Наталья',
          avatar: 'https://randomuser.me/api/portraits/women/87.jpg',
          quality: 'common',
          level: 3,
          incomePerHour: 20,
          incomeMultiplier: 1.0,
          lastSeen: new Date(Date.now() - 1000 * 60 * 45).toISOString(), // 45 минут назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 4).toISOString() // 4 дня назад
        },
        {
          id: '9',
          userId: '101010101',
          name: 'Сергей',
          avatar: 'https://randomuser.me/api/portraits/men/18.jpg',
          quality: 'rare',
          level: 2,
          incomePerHour: 35,
          incomeMultiplier: 2.5,
          lastSeen: new Date(Date.now() - 1000 * 60 * 60 * 5).toISOString(), // 5 часов назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 6).toISOString() // 6 дней назад
        }
      ];
      
      // Добавляем каждый дроп
      testDrops.forEach(drop => {
        addDrop(drop);
      });
    }

    // Имитируем задержку загрузки
    const timer = setTimeout(() => {
      setLoading(false);
    }, 800);
    
    return () => clearTimeout(timer);
  }, [drops.length, addDrop]);

  const handleUpgrade = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    console.log(`Улучшение дропа ${dropId}`);
  };

  const handleRelease = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    console.log(`Отпускание дропа ${dropId}`);
    setDrops(drops.filter(drop => drop.id !== dropId));
  };

  const handleExtraPayment = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    console.log(`Доплата за дроп ${dropId}`);
  };

  const toggleDropSelection = (dropId: string) => {
    if (selectedDrops.includes(dropId)) {
      setSelectedDrops(selectedDrops.filter(id => id !== dropId));
    } else {
      setSelectedDrops([...selectedDrops, dropId]);
    }
  };

  const toggleMultiSelectMode = () => {
    setIsMultiSelectMode(!isMultiSelectMode);
    setSelectedDrops([]);
  };

  const handleBulkAction = (action: 'upgrade' | 'release' | 'payExtra') => {
    // В реальном приложении здесь будет API запрос
    console.log(`Действие ${action} для дропов:`, selectedDrops);
    
    if (action === 'release') {
      setDrops(drops.filter(drop => !selectedDrops.includes(drop.id)));
    }
    
    setSelectedDrops([]);
    setIsMultiSelectMode(false);
  };

  return (
    <div className="drops-page">
      <div className="drops-header">
        <h1>Мои Дропы</h1>
        <div className="balance">
          Баланс: <span>{new Intl.NumberFormat('ru-RU').format(balance)}</span> ₽
        </div>
      </div>

      <div className="drops-stats">
        <div className="stat-item">
          <span className="stat-label">Всего дропов:</span>
          <span className="stat-value">{totalDropsCount}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Доход/час:</span>
          <span className="stat-value">{new Intl.NumberFormat('ru-RU').format(dropsIncomePerHour)} ₽</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Активных:</span>
          <span className="stat-value">
            {drops.filter(drop => {
              const lastSeen = new Date(drop.lastSeen);
              const now = new Date();
              return (now.getTime() - lastSeen.getTime()) / (1000 * 60 * 60) < 24;
            }).length}
          </span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Потеряно за 24ч:</span>
          <span className="stat-value">{lostDrops24h}</span>
        </div>
      </div>

      <div className="drops-controls">
        <Button 
          onClick={toggleMultiSelectMode} 
          className={isMultiSelectMode ? 'active' : ''}
        >
          {isMultiSelectMode ? 'Отменить выбор' : 'Выбрать несколько'}
        </Button>
        
        {isMultiSelectMode && selectedDrops.length > 0 && (
          <div className="bulk-actions">
            <Button onClick={() => handleBulkAction('upgrade')}>
              Улучшить ({selectedDrops.length})
            </Button>
            <Button onClick={() => handleBulkAction('payExtra')}>
              Доплатить ({selectedDrops.length})
            </Button>
            <Button onClick={() => handleBulkAction('release')}>
              Отпустить ({selectedDrops.length})
            </Button>
          </div>
        )}
      </div>

      {loading ? (
        <div className="drops-loading">
          <Loader />
        </div>
      ) : (
        <div className="drops-grid">
          {drops.length > 0 ? (
            drops.map(drop => (
              <div 
                key={drop.id} 
                className={`drop-card-wrapper ${isMultiSelectMode ? 'selectable' : ''} ${selectedDrops.includes(drop.id) ? 'selected' : ''}`}
              >
                <DropCard
                  drop={drop as any} // Временное решение для типизации
                  onUpgrade={handleUpgrade}
                  onRelease={handleRelease}
                  onExtraPayment={handleExtraPayment}
                  isSelectable={isMultiSelectMode}
                  isSelected={selectedDrops.includes(drop.id)}
                  onSelect={toggleDropSelection}
                />
              </div>
            ))
          ) : (
            <div className="no-drops">
              <p>У вас пока нет дропов</p>
              <Button onClick={() => navigate('/invite')}>Пригласить друзей</Button>
            </div>
          )}
        </div>
      )}
      
      <BottomNav />
    </div>
  );
};

export default DropsPage;
