import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { Business, AvailableBusiness } from '../types/business';
import { Drop, DropQuality } from '../types/drops';

// Определение типа контекста
interface GameContextType {
  balance: number;
  setBalance: (balance: number) => void;
  ownedBusinesses: Business[];
  setOwnedBusinesses: (businesses: Business[]) => void;
  availableBusinesses: AvailableBusiness[];
  setAvailableBusinesses: (businesses: AvailableBusiness[]) => void;
  drops: Drop[];
  setDrops: (drops: Drop[]) => void;
  lostDrops24h: number;
  totalDropsCount: number;
  totalIncomePerHour: number;
  dropsIncomePerHour: number;
  addBusiness: (business: Business) => void;
  removeBusiness: (businessId: string) => void;
  updateBusiness: (updatedBusiness: Business) => void;
  getMostProfitableBusiness: () => Business | null;
  addDrop: (drop: Drop) => void;
  removeDrop: (dropId: string) => void;
  upgradeDrop: (dropId: string) => void;
}

// Создание контекста
const GameContext = createContext<GameContextType | undefined>(undefined);

// Провайдер контекста
interface GameProviderProps {
  children: ReactNode;
}

// Качество дропов и их множители
const QUALITY_MULTIPLIERS = {
  common: 1.0,
  rare: 2.5,
  epic: 6.0,
  legendary: 15.0
};

// Базовый доход дропа
const BASE_DROP_INCOME = 10; // 10 монет в час

export const GameProvider: React.FC<GameProviderProps> = ({ children }) => {
  const [balance, setBalance] = useState<number>(1000);
  const [ownedBusinesses, setOwnedBusinesses] = useState<Business[]>([]);
  const [availableBusinesses, setAvailableBusinesses] = useState<AvailableBusiness[]>([]);
  const [drops, setDrops] = useState<Drop[]>([]);
  const [lostDrops24h, setLostDrops24h] = useState<number>(0);
  const [totalIncomePerHour, setTotalIncomePerHour] = useState<number>(0);
  const [dropsIncomePerHour, setDropsIncomePerHour] = useState<number>(0);

  // Рассчитываем общий доход от всех бизнесов
  useEffect(() => {
    const businessIncome = ownedBusinesses.reduce((sum, business) => sum + business.income_per_hour, 0);
    setTotalIncomePerHour(businessIncome + dropsIncomePerHour);
  }, [ownedBusinesses, dropsIncomePerHour]);

  // Расчет дохода от дропов
  useEffect(() => {
    const dropsIncome = drops.reduce((sum, drop) => sum + drop.incomePerHour, 0);
    setDropsIncomePerHour(dropsIncome);
  }, [drops]);

  // Симуляция поступления дохода
  useEffect(() => {
    const incomeInterval = setInterval(() => {
      if (totalIncomePerHour > 0) {
        setBalance(prevBalance => {
          const incomePerSecond = totalIncomePerHour / 3600;
          return parseFloat((prevBalance + incomePerSecond).toFixed(2));
        });
      }
    }, 1000);

    return () => clearInterval(incomeInterval);
  }, [totalIncomePerHour]);

  // Общее количество дропов
  const totalDropsCount = drops.length;

  // Функции для работы с бизнесами
  const addBusiness = (business: Business) => {
    setOwnedBusinesses(prev => [...prev, business]);
  };

  const removeBusiness = (businessId: string) => {
    setOwnedBusinesses(prev => prev.filter(b => b.business_id !== businessId));
  };

  const updateBusiness = (updatedBusiness: Business) => {
    setOwnedBusinesses(prev => 
      prev.map(b => b.business_id === updatedBusiness.business_id ? updatedBusiness : b)
    );
  };

  const getMostProfitableBusiness = (): Business | null => {
    if (ownedBusinesses.length === 0) return null;
    
    return ownedBusinesses.reduce((mostProfitable, current) => 
      current.income_per_hour > mostProfitable.income_per_hour ? current : mostProfitable
    , ownedBusinesses[0]);
  };

  // Функции для работы с дропами
  const addDrop = (drop: Drop) => {
    setDrops(prev => [...prev, drop]);
  };

  const removeDrop = (dropId: string) => {
    setDrops(prev => prev.filter(d => d.id !== dropId));
    setLostDrops24h(prev => prev + 1);
  };

  const upgradeDrop = (dropId: string) => {
    const dropIndex = drops.findIndex(d => d.id === dropId);
    if (dropIndex === -1) return;

    const drop = drops[dropIndex];
    const upgradeCost = Math.floor(100 * Math.pow(1.5, drop.level));

    if (balance >= upgradeCost) {
      const updatedDrop = {
        ...drop,
        level: drop.level + 1,
        incomeMultiplier: (drop.incomeMultiplier || 1) * 1.2, // +20% к множителю
        incomePerHour: BASE_DROP_INCOME * QUALITY_MULTIPLIERS[drop.quality] * drop.level * 1.2
      };

      const updatedDrops = [...drops];
      updatedDrops[dropIndex] = updatedDrop;

      setDrops(updatedDrops);
      setBalance(balance - upgradeCost);
    }
  };

  return (
    <GameContext.Provider value={{
      balance,
      setBalance,
      ownedBusinesses,
      setOwnedBusinesses,
      availableBusinesses,
      setAvailableBusinesses,
      drops,
      setDrops,
      lostDrops24h,
      totalDropsCount,
      totalIncomePerHour,
      dropsIncomePerHour,
      addBusiness,
      removeBusiness,
      updateBusiness,
      getMostProfitableBusiness,
      addDrop,
      removeDrop,
      upgradeDrop
    }}>
      {children}
    </GameContext.Provider>
  );
};

// Хук для использования контекста
export const useGame = (): GameContextType => {
  const context = useContext(GameContext);
  if (context === undefined) {
    throw new Error('useGame must be used within a GameProvider');
  }
  return context;
};

export default GameContext;
