import React from 'react';
import './DropFilters.scss';

// Типы сортировки для дропов
export type DropSortOption = 'level_asc' | 'level_desc' | 'income' | 'upgrade_cost';

interface DropFiltersProps {
  sortBy: DropSortOption;
  onChangeSortBy: (sortBy: DropSortOption) => void;
}

const DropFilters: React.FC<DropFiltersProps> = ({
  sortBy,
  onChangeSortBy
}) => {
  return (
    <div className="drop-filters">
      <div className="drop-filters__options">
        <div className="drop-filters__group">
          <label className="drop-filters__label">Сортировка:</label>
          <div className="drop-filters__buttons">
            <button 
              className={`drop-filters__button ${sortBy === 'level_desc' ? 'active' : ''}`}
              onClick={() => onChangeSortBy('level_desc')}
            >
              Уровень ↓
            </button>
            <button 
              className={`drop-filters__button ${sortBy === 'level_asc' ? 'active' : ''}`}
              onClick={() => onChangeSortBy('level_asc')}
            >
              Уровень ↑
            </button>
            <button 
              className={`drop-filters__button ${sortBy === 'income' ? 'active' : ''}`}
              onClick={() => onChangeSortBy('income')}
            >
              Доход
            </button>
            <button 
              className={`drop-filters__button ${sortBy === 'upgrade_cost' ? 'active' : ''}`}
              onClick={() => onChangeSortBy('upgrade_cost')}
            >
              Улучшение
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DropFilters;
