import React from 'react';
import './DropCard.scss';
import { Drop, DropCardProps } from '../../types/drop';

const DropCard: React.FC<DropCardProps> = ({ 
  drop, 
  onUpgrade, 
  onRelease, 
  onExtraPayment,
  isSelected = false,
  isSelectable = false,
  onSelect
}) => {
  // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ –¥—Ä–æ–ø–∞ (–∞–∫—Ç–∏–≤–µ–Ω, –µ—Å–ª–∏ –±—ã–ª –æ–Ω–ª–∞–π–Ω –≤ —Ç–µ—á–µ–Ω–∏–µ –ø–æ—Å–ª–µ–¥–Ω–∏—Ö 24 —á–∞—Å–æ–≤)
  const isActive = (): boolean => {
    const now = new Date();
    const lastSeen = new Date(drop.lastSeen);
    const diffHours = Math.abs(now.getTime() - lastSeen.getTime()) / 36e5; // 36e5 = 60*60*1000 (–º—Å –≤ —á–∞—Å–µ)
    return diffHours < 24;
  };

  // –ü–æ–ª—É—á–µ–Ω–∏–µ –∫–ª–∞—Å—Å–∞ –∫–∞—á–µ—Å—Ç–≤–∞ –¥–ª—è —Å—Ç–∏–ª–∏–∑–∞—Ü–∏–∏
  const getQualityClass = (): string => {
    switch (drop.quality) {
      case 'common':
        return 'drop-card--common';
      case 'rare':
        return 'drop-card--rare';
      case 'epic':
        return 'drop-card--epic';
      case 'legendary':
        return 'drop-card--legendary';
      default:
        return 'drop-card--common';
    }
  };

  const active = isActive();
  const qualityClass = getQualityClass();
  
  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∫–ª–∏–∫–∞ –¥–ª—è –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–µ–Ω–∏—è –≤—Å–ø–ª—ã—Ç–∏—è —Å–æ–±—ã—Ç–∏–π
  const handleActionClick = (e: React.MouseEvent, callback: (id: string) => void) => {
    e.stopPropagation();
    callback(drop.id);
  };
  
  return (
    <div 
      className={`drop-card ${qualityClass} ${!active ? 'drop-card--inactive' : ''} ${isSelected ? 'drop-card--selected' : ''}`}
      onClick={isSelectable && onSelect ? () => onSelect(drop.id) : undefined}
    >
      <div className="drop-card__avatar">
        {drop.avatar ? (
          <img src={drop.avatar} alt={drop.name} />
        ) : (
          <div className="drop-card__avatar-placeholder">
            {drop.name.charAt(0).toUpperCase()}
          </div>
        )}
      </div>
      
      <div className="drop-card__info">
        <h3 className="drop-card__name">{drop.name}</h3>
        <div className="drop-card__stats">
          <div className="drop-card__stat">
            <span className="drop-card__stat-value">{drop.level} LVL</span>
          </div>
          <div className="drop-card__stat">
            <span className="drop-card__stat-value">{drop.incomePerHour} ‚ÇΩ/—á</span>
          </div>
        </div>
      </div>
      
      {!isSelectable && (
        <div className="drop-card__actions">
          <button 
            className="drop-card__action drop-card__action--upgrade"
            onClick={(e) => handleActionClick(e, onUpgrade)}
            aria-label="–£–ª—É—á—à–∏—Ç—å"
          >
            ‚¨ÜÔ∏è
          </button>
          <button 
            className="drop-card__action drop-card__action--pay"
            onClick={(e) => handleActionClick(e, onExtraPayment)}
            disabled={!active}
            aria-label="–î–æ–ø–ª–∞—Ç–∏—Ç—å"
          >
            üí∞
          </button>
          <button 
            className="drop-card__action drop-card__action--release"
            onClick={(e) => handleActionClick(e, onRelease)}
            aria-label="–û—Ç–ø—É—Å—Ç–∏—Ç—å"
          >
            üö´
          </button>
        </div>
      )}
      
      {isSelectable && isSelected && (
        <div className="drop-card__select-indicator">‚úì</div>
      )}
    </div>
  );
};

export default DropCard;
