import React from 'react';
import { 
  Drop, 
  DropQuality, 
  DROP_QUALITY_NAMES, 
  formatTimeLeft,
  DropCardProps
} from '../../../types/drop';
import './DropCard.scss';

const DropCard: React.FC<DropCardProps> = ({ drop, onUpgrade, onRenew, onRelease }) => {
  return (
    <div className={`drop-card ${drop.quality.toLowerCase()} ${!drop.isActive ? 'inactive' : ''}`}>
      <div className="drop-card__avatar">
        {drop.avatarUrl ? (
          <img src={drop.avatarUrl} alt={drop.name} />
        ) : (
          <div className="drop-card__avatar-placeholder">
            {drop.name.charAt(0).toUpperCase()}
          </div>
        )}
      </div>
      
      <div className="drop-card__content">
        <div className="drop-card__header">
          <div className="drop-card__name">{drop.name}</div>
          <div className="drop-card__quality">{DROP_QUALITY_NAMES[drop.quality]}</div>
        </div>
        
        <div className="drop-card__stats">
          <div className="drop-card__stat">
            <span className="drop-card__stat-label">–£—Ä–æ–≤–µ–Ω—å:</span>
            <span className="drop-card__stat-value">{drop.level}</span>
          </div>
          <div className="drop-card__stat">
            <span className="drop-card__stat-label">–î–æ—Ö–æ–¥:</span>
            <span className="drop-card__stat-value">{drop.income} üí∞/—á–∞—Å</span>
          </div>
          {drop.expiresAt && (
            <div className="drop-card__stat">
              <span className="drop-card__stat-label">–û—Å—Ç–∞–ª–æ—Å—å:</span>
              <span className="drop-card__stat-value">{formatTimeLeft(drop.expiresAt)}</span>
            </div>
          )}
        </div>
        
        {drop.isActive && (
          <div className="drop-card__actions">
            <button 
              className="drop-card__action drop-card__action--upgrade" 
              onClick={() => onUpgrade(drop.id)}
            >
              –£–ª—É—á—à–∏—Ç—å ({drop.upgradePrice} üí∞)
            </button>
            <button 
              className="drop-card__action drop-card__action--renew" 
              onClick={() => onRenew(drop.id)}
            >
              –ü—Ä–æ–¥–ª–∏—Ç—å ({drop.renewPrice} üí∞)
            </button>
            <button 
              className="drop-card__action drop-card__action--release" 
              onClick={() => onRelease(drop.id)}
            >
              –û—Ç–ø—É—Å—Ç–∏—Ç—å
            </button>
          </div>
        )}
        
        {!drop.isActive && (
          <div className="drop-card__inactive-message">
            –î—Ä–æ–ø –Ω–µ–∞–∫—Ç–∏–≤–µ–Ω. –ü—Ä–æ–¥–ª–∏—Ç–µ –µ–≥–æ, —á—Ç–æ–±—ã –ø–æ–ª—É—á–∞—Ç—å –¥–æ—Ö–æ–¥.
          </div>
        )}
      </div>
    </div>
  );
};

export default DropCard;
