import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Drops.scss';
import DropCard from '../../components/Drops/DropCard';
import Button from '../../components/common/Button';
import Loader from '../../components/common/Loader';
import { useGame } from '../../context/GameContext';
import BottomNavBar from '../../components/common/BottomNavBar';

const DropsPage: React.FC = () => {
  const navigate = useNavigate();
  const { drops, setDrops, balance, lostDrops24h, totalDropsCount, dropsIncomePerHour } = useGame();
  
  const [loading, setLoading] = useState<boolean>(true);
  const [selectedDrops, setSelectedDrops] = useState<string[]>([]);
  const [isMultiSelectMode, setIsMultiSelectMode] = useState<boolean>(false);

  // Имитация загрузки данных
  useEffect(() => {
    // Имитируем задержку загрузки
    const timer = setTimeout(() => {
      setLoading(false);
    }, 800);
    
    return () => clearTimeout(timer);
  }, []);

  const handleUpgrade = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    console.log(`Улучшение дропа ${dropId}`);
  };

  const handleRelease = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    console.log(`Отпускание дропа ${dropId}`);
    setDrops(drops.filter(drop => drop.id !== dropId));
  };

  const handleExtraPayment = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    console.log(`Доплата за дроп ${dropId}`);
  };

  const toggleDropSelection = (dropId: string) => {
    if (selectedDrops.includes(dropId)) {
      setSelectedDrops(selectedDrops.filter(id => id !== dropId));
    } else {
      setSelectedDrops([...selectedDrops, dropId]);
    }
  };

  const toggleMultiSelectMode = () => {
    setIsMultiSelectMode(!isMultiSelectMode);
    setSelectedDrops([]);
  };

  const handleBulkAction = (action: 'upgrade' | 'release' | 'payExtra') => {
    // В реальном приложении здесь будет API запрос
    console.log(`Действие ${action} для дропов:`, selectedDrops);
    
    if (action === 'release') {
      setDrops(drops.filter(drop => !selectedDrops.includes(drop.id)));
    }
    
    setSelectedDrops([]);
    setIsMultiSelectMode(false);
  };

  return (
    <div className="page-container">
      <div className="drops-page">
        <div className="drops-header">
          <h1>{balance.toFixed(2)}</h1>
          <div className="balance">Ваш баланс</div>
        </div>

        <div className="drops-stats">
          <div className="stat-item">
            <span className="stat-label">Всего дропов</span>
            <span className="stat-value">{totalDropsCount}</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Доход в час</span>
            <span className="stat-value">{new Intl.NumberFormat('ru-RU').format(dropsIncomePerHour)} ₽</span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Активных дропов</span>
            <span className="stat-value">
              {drops.filter(drop => {
                const lastSeen = new Date(drop.lastSeen);
                const now = new Date();
                return (now.getTime() - lastSeen.getTime()) / (1000 * 60 * 60) < 24;
              }).length}
            </span>
          </div>
          <div className="stat-item">
            <span className="stat-label">Потеряно за 24ч</span>
            <span className="stat-value">{lostDrops24h}</span>
          </div>
        </div>

        <div className="drops-section">
          <div className="drops-section-header">
            <h2>Управление дропами</h2>
          </div>

          <div className="drops-controls">
            <Button 
              onClick={toggleMultiSelectMode} 
              className={isMultiSelectMode ? 'active' : ''}
            >
              {isMultiSelectMode ? 'Отменить выбор' : 'Выбрать несколько'}
            </Button>
            
            {isMultiSelectMode && selectedDrops.length > 0 && (
              <div className="bulk-actions">
                <Button onClick={() => handleBulkAction('upgrade')}>
                  Улучшить ({selectedDrops.length})
                </Button>
                <Button onClick={() => handleBulkAction('payExtra')}>
                  Доплатить ({selectedDrops.length})
                </Button>
                <Button onClick={() => handleBulkAction('release')}>
                  Отпустить ({selectedDrops.length})
                </Button>
              </div>
            )}
          </div>
        </div>

        {loading ? (
          <div className="drops-loading">
            <Loader />
          </div>
        ) : (
          <div className="drops-grid">
            {drops.length > 0 ? (
              drops.map(drop => (
                <div 
                  key={drop.id} 
                  className={`drop-card-wrapper ${isMultiSelectMode ? 'selectable' : ''} ${selectedDrops.includes(drop.id) ? 'selected' : ''}`}
                >
                  <DropCard
                    drop={drop as any} // Временное решение для типизации
                    onUpgrade={handleUpgrade}
                    onRelease={handleRelease}
                    onExtraPayment={handleExtraPayment}
                    isSelectable={isMultiSelectMode}
                    isSelected={selectedDrops.includes(drop.id)}
                    onSelect={toggleDropSelection}
                  />
                </div>
              ))
            ) : (
              <div className="no-drops">
                <p>У вас пока нет дропов</p>
                <Button onClick={() => navigate('/invite')}>Пригласить друзей</Button>
              </div>
            )}
          </div>
        )}
      </div>
      
      <BottomNavBar />
    </div>
  );
};

export default DropsPage;
