// Типы качества дропа
export enum DropQuality {
  COMMON = 'common',
  RARE = 'rare',
  EPIC = 'epic',
  LEGENDARY = 'legendary'
}

// Интерфейс для дропа
export interface Drop {
  id: string;
  userId: string; // ID пользователя Telegram
  name: string;
  avatar: string | undefined;
  quality: DropQuality | string;
  level: number;
  incomePerHour: number;
  incomeMultiplier: number;
  baseIncomePerHour?: number; // Базовый доход в час
  lastSeen: Date | string;
  createdAt: Date | string;
  updatedAt?: Date | string;
  isActive?: boolean; // Активен ли дроп (пользователь не ушел)
}

// Интерфейс для статистики дропов
export interface DropStatistics {
  totalCount: number;
  lostIn24h: number;
  totalIncome: number;
  qualityDistribution: {
    [DropQuality.COMMON]: number;
    [DropQuality.RARE]: number;
    [DropQuality.EPIC]: number;
    [DropQuality.LEGENDARY]: number;
  };
}

// Интерфейс для обновления дропа
export interface DropUpgrade {
  dropId: string;
  newLevel: number;
  cost: number;
}

// Интерфейс для информации об обновлении дропа
export interface DropUpgradeInfo {
  currentLevel: number;
  nextLevel: number;
  currentIncome: number;
  nextIncome: number;
  upgradeCost: number;
  successRate: number;
}

// Интерфейс для страницы дропов
export interface DropsPageProps {
  // Дополнительные пропсы, если потребуются
}

// Интерфейс для карточки дропа
export interface DropCardProps {
  drop: Drop;
  onUpgrade: (id: string) => void;
  onRelease: (id: string) => void;
  onExtraPayment: (id: string) => void;
  isSelected?: boolean;
  isSelectable?: boolean;
  onSelect?: (id: string) => void;
}

// Константы для расчета стоимости улучшения и дохода
export const DROP_CONSTANTS = {
  BASE_UPGRADE_COST: 100, // Базовая стоимость улучшения
  UPGRADE_MULTIPLIER: 1.5, // Множитель увеличения стоимости с каждым уровнем
  QUALITY_MULTIPLIERS: {
    [DropQuality.COMMON]: 1,
    [DropQuality.RARE]: 2,
    [DropQuality.EPIC]: 4,
    [DropQuality.LEGENDARY]: 8
  },
  LEVEL_INCOME_MULTIPLIER: 0.2, // Множитель увеличения дохода за каждый уровень (например, +20%)
};
