export enum DropQuality {
  COMMON = 'common',
  RARE = 'rare',
  EPIC = 'epic',
  LEGENDARY = 'legendary'
}

export interface Drop {
  id: string;
  userId: string;
  username: string;
  quality: DropQuality;
  level: number;
  baseIncome: number;
  lastCollected: Date;
  createdAt: Date;
  updatedAt: Date;
}

export interface DropStats {
  totalDrops: number;
  lostDrops24h: number;
  totalIncome: number;
  incomeByQuality: Record<DropQuality, number>;
}

// Константы для множителей качества
export const QUALITY_MULTIPLIERS: Record<DropQuality, number> = {
  [DropQuality.COMMON]: 1,
  [DropQuality.RARE]: 1.5,
  [DropQuality.EPIC]: 2.5,
  [DropQuality.LEGENDARY]: 5
};

// Константы для цветов качества
export const QUALITY_COLORS: Record<DropQuality, string> = {
  [DropQuality.COMMON]: '#808080', // Серый
  [DropQuality.RARE]: '#4169E1', // Синий
  [DropQuality.EPIC]: '#9932CC', // Фиолетовый
  [DropQuality.LEGENDARY]: '#FFD700' // Золотой
};

// Константа для стоимости улучшения уровня
export const LEVEL_UPGRADE_COST_BASE = 100; // Базовая стоимость улучшения
export const LEVEL_INCOME_MULTIPLIER = 0.1; // Каждый уровень увеличивает доход на 10%
