import React from 'react';
import './DropCard.scss';
import { Drop, DropQuality, QUALITY_COLORS, QUALITY_MULTIPLIERS, LEVEL_INCOME_MULTIPLIER } from '../../../types/drop';
import Icon from '../../common/Icon';
import Button from '../../common/Button';

interface DropCardProps {
  drop: Drop;
  onUpgrade: (dropId: string) => void;
  onRelease: (dropId: string) => void;
  onCollect: (dropId: string) => void;
}

const DropCard: React.FC<DropCardProps> = ({ drop, onUpgrade, onRelease, onCollect }) => {
  const { id, username, quality, level, baseIncome, lastCollected } = drop;
  
  // Рассчитываем текущий доход с учетом уровня и качества
  const currentIncome = baseIncome * QUALITY_MULTIPLIERS[quality] * (1 + level * LEVEL_INCOME_MULTIPLIER);
  
  // Проверяем, прошло ли 24 часа с момента последнего сбора дохода
  const canCollect = new Date().getTime() - new Date(lastCollected).getTime() >= 24 * 60 * 60 * 1000;
  
  // Строка для отображения качества на русском
  const qualityText = {
    [DropQuality.COMMON]: 'Обычный',
    [DropQuality.RARE]: 'Редкий',
    [DropQuality.EPIC]: 'Эпический',
    [DropQuality.LEGENDARY]: 'Легендарный'
  }[quality];
  
  return (
    <div className="drop-card" style={{ borderColor: QUALITY_COLORS[quality] }}>
      <div className="drop-card__header" style={{ backgroundColor: QUALITY_COLORS[quality] }}>
        <span className="drop-card__quality">{qualityText}</span>
        <span className="drop-card__level">Ур. {level}</span>
      </div>
      
      <div className="drop-card__content">
        <div className="drop-card__username">{username}</div>
        
        <div className="drop-card__income">
          <span className="drop-card__income-label">Доход:</span>
          <span className="drop-card__income-value">{currentIncome.toFixed(2)} ₽/день</span>
        </div>
        
        <div className="drop-card__actions">
          <Button 
            variant="primary" 
            size="small" 
            onClick={() => onCollect(id)} 
            disabled={!canCollect}
            className="drop-card__collect-btn"
          >
            Собрать
          </Button>
          
          <div className="drop-card__buttons">
            <button className="drop-card__action-btn" onClick={() => onUpgrade(id)}>
              <Icon name="upgrade" size="md" />
            </button>
            <button className="drop-card__action-btn" onClick={() => onRelease(id)}>
              <Icon name="release" size="md" />
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default DropCard;
