import React, { useState } from 'react';
import './Drops.scss';
import { useGame } from '../../context/GameContext';
import { Drop, DropQuality } from '../../types/drop';
import DropCard from '../../components/Drops/DropCard';
import Button from '../../components/common/Button';
import BottomNav from '../../components/common/BottomNav';

const DropsPage: React.FC = () => {
  // –ò—Å–ø–æ–ª—å–∑—É–µ–º –∫–æ–Ω—Ç–µ–∫—Å—Ç –∏–≥—Ä—ã –≤–º–µ—Å—Ç–æ –ª–æ–∫–∞–ª—å–Ω–æ–≥–æ —Å–æ—Å—Ç–æ—è–Ω–∏—è
  const { 
    balance, 
    drops, 
    dropsStats, 
    upgradeDropLevel, 
    renewDrop, 
    releaseDrop 
  } = useGame();
  
  const [filterActive, setFilterActive] = useState<boolean | null>(null);
  const [sortBy, setSortBy] = useState<string>('income');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  
  // –§–∏–ª—å—Ç—Ä–∞—Ü–∏—è –∏ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞ –¥—Ä–æ–ø–æ–≤
  const filteredAndSortedDrops = [...drops]
    .filter(drop => {
      if (filterActive === null) return true;
      return drop.isActive === filterActive;
    })
    .sort((a, b) => {
      let comparison = 0;
      
      switch (sortBy) {
        case 'income':
          comparison = a.income - b.income;
          break;
        case 'level':
          comparison = a.level - b.level;
          break;
        case 'quality':
          const qualityOrder = {
            [DropQuality.COMMON]: 0,
            [DropQuality.RARE]: 1,
            [DropQuality.EPIC]: 2,
            [DropQuality.LEGENDARY]: 3
          };
          comparison = qualityOrder[a.quality] - qualityOrder[b.quality];
          break;
        case 'expires':
          const aTime = a.expiresAt ? a.expiresAt.getTime() : Infinity;
          const bTime = b.expiresAt ? b.expiresAt.getTime() : Infinity;
          comparison = aTime - bTime;
          break;
        default:
          comparison = 0;
      }
      
      return sortOrder === 'asc' ? comparison : -comparison;
    });

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∑–∞–ø—Ä–æ—Å–∞ –¥–∏–∞–ª–æ–≥–∞ –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è –¥–ª—è –æ—Ç–ø—É—Å–∫–∞–Ω–∏—è –¥—Ä–æ–ø–∞
  const handleConfirmRelease = (dropId: string) => {
    if (window.confirm('–í—ã —É–≤–µ—Ä–µ–Ω—ã, —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ –æ—Ç–ø—É—Å—Ç–∏—Ç—å —ç—Ç–æ—Ç –¥—Ä–æ–ø? –≠—Ç–æ –¥–µ–π—Å—Ç–≤–∏–µ –Ω–µ–æ–±—Ä–∞—Ç–∏–º–æ.')) {
      releaseDrop(dropId);
    }
  };
  
  // –ì—Ä—É–ø–ø–æ–≤—ã–µ –æ–ø–µ—Ä–∞—Ü–∏–∏ —Å –¥—Ä–æ–ø–∞–º–∏
  const handleMassUpgrade = () => {
    const activeDrops = drops.filter(drop => drop.isActive);
    const totalCost = activeDrops.reduce((sum, drop) => sum + drop.upgradePrice, 0);
    
    if (balance < totalCost) {
      alert(`–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤ –¥–ª—è —É–ª—É—á—à–µ–Ω–∏—è –≤—Å–µ—Ö –¥—Ä–æ–ø–æ–≤. –¢—Ä–µ–±—É–µ—Ç—Å—è: ${totalCost} üí∞`);
      return;
    }
    
    if (window.confirm(`–£–ª—É—á—à–∏—Ç—å –≤—Å–µ –∞–∫—Ç–∏–≤–Ω—ã–µ –¥—Ä–æ–ø—ã? –°—Ç–æ–∏–º–æ—Å—Ç—å: ${totalCost} üí∞`)) {
      // –£–ª—É—á—à–∞–µ–º –≤—Å–µ –∞–∫—Ç–∏–≤–Ω—ã–µ –¥—Ä–æ–ø—ã
      activeDrops.forEach(drop => upgradeDropLevel(drop.id));
    }
  };
  
  const handleMassRenew = () => {
    const inactiveDrops = drops.filter(drop => !drop.isActive);
    const totalCost = inactiveDrops.reduce((sum, drop) => sum + drop.renewPrice, 0);
    
    if (balance < totalCost) {
      alert(`–ù–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ —Å—Ä–µ–¥—Å—Ç–≤ –¥–ª—è –ø—Ä–æ–¥–ª–µ–Ω–∏—è –≤—Å–µ—Ö –¥—Ä–æ–ø–æ–≤. –¢—Ä–µ–±—É–µ—Ç—Å—è: ${totalCost} üí∞`);
      return;
    }
    
    if (window.confirm(`–ü—Ä–æ–¥–ª–∏—Ç—å –≤—Å–µ –Ω–µ–∞–∫—Ç–∏–≤–Ω—ã–µ –¥—Ä–æ–ø—ã? –°—Ç–æ–∏–º–æ—Å—Ç—å: ${totalCost} üí∞`)) {
      // –ü—Ä–æ–¥–ª–µ–≤–∞–µ–º –≤—Å–µ –Ω–µ–∞–∫—Ç–∏–≤–Ω—ã–µ –¥—Ä–æ–ø—ã
      inactiveDrops.forEach(drop => renewDrop(drop.id));
    }
  };
  
  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–æ–≤ –∏ —Å–æ—Ä—Ç–∏—Ä–æ–≤–∫–∏
  const handleFilterChange = (value: boolean | null) => {
    setFilterActive(value);
  };
  
  const handleSortChange = (field: string) => {
    if (sortBy === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('desc');
    }
  };
  
  const handleInviteFriends = () => {
    alert('–ü–æ–¥–µ–ª–∏—Ç–µ—Å—å —Å—Å—ã–ª–∫–æ–π –Ω–∞ –∏–≥—Ä—É —Å –¥—Ä—É–∑—å—è–º–∏, —á—Ç–æ–±—ã –ø–æ–ª—É—á–∏—Ç—å –¥—Ä–æ–ø—ã!');
  };
  
  return (
    <div className="drops-page">
      <div className="drops-page__header">
        <h1 className="drops-page__title">–ú–æ–∏ –¥—Ä–æ–ø—ã</h1>
        <div className="drops-page__balance">
          –ë–∞–ª–∞–Ω—Å: <span className="drops-page__balance-value">{balance.toFixed(2)} üí∞</span>
        </div>
      </div>
      
      <div className="drops-page__stats">
        <div className="stats-card">
          <div className="stats-card__item">
            <div className="stats-card__label">–í—Å–µ–≥–æ –¥—Ä–æ–ø–æ–≤</div>
            <div className="stats-card__value">{dropsStats.totalDrops}</div>
          </div>
          <div className="stats-card__item">
            <div className="stats-card__label">–ê–∫—Ç–∏–≤–Ω—ã–µ</div>
            <div className="stats-card__value">{dropsStats.activeDrops}</div>
          </div>
          <div className="stats-card__item">
            <div className="stats-card__label">–ü–æ—Ç–µ—Ä—è–Ω–æ –∑–∞ 24—á</div>
            <div className="stats-card__value">{dropsStats.lostDrops24h}</div>
          </div>
          <div className="stats-card__item">
            <div className="stats-card__label">–û–±—â–∏–π –¥–æ—Ö–æ–¥</div>
            <div className="stats-card__value stats-card__value--income">{dropsStats.totalIncome} üí∞/—á–∞—Å</div>
          </div>
        </div>
      </div>
      
      <div className="drops-page__actions">
        <div className="drops-page__mass-actions">
          <Button 
            variant="primary" 
            onClick={handleMassUpgrade}
            disabled={!drops.some(drop => drop.isActive)}
          >
            –£–ª—É—á—à–∏—Ç—å –≤—Å–µ
          </Button>
          <Button 
            variant="secondary" 
            onClick={handleMassRenew}
            disabled={!drops.some(drop => !drop.isActive)}
          >
            –ü—Ä–æ–¥–ª–∏—Ç—å –≤—Å–µ
          </Button>
        </div>
        
        <div className="drops-page__filters">
          <div className="drops-page__filter-group">
            <span className="drops-page__filter-label">–°—Ç–∞—Ç—É—Å:</span>
            <Button 
              variant={filterActive === null ? "primary" : "outline"} 
              size="small" 
              onClick={() => handleFilterChange(null)}
            >
              –í—Å–µ
            </Button>
            <Button 
              variant={filterActive === true ? "primary" : "outline"} 
              size="small" 
              onClick={() => handleFilterChange(true)}
            >
              –ê–∫—Ç–∏–≤–Ω—ã–µ
            </Button>
            <Button 
              variant={filterActive === false ? "primary" : "outline"} 
              size="small" 
              onClick={() => handleFilterChange(false)}
            >
              –ù–µ–∞–∫—Ç–∏–≤–Ω—ã–µ
            </Button>
          </div>
          
          <div className="drops-page__filter-group">
            <span className="drops-page__filter-label">–°–æ—Ä—Ç–∏—Ä–æ–≤–∫–∞:</span>
            <select 
              className="drops-page__sort-select" 
              value={`${sortBy}-${sortOrder}`}
              onChange={(e) => {
                const [field, order] = e.target.value.split('-');
                setSortBy(field);
                setSortOrder(order as 'asc' | 'desc');
              }}
            >
              <option value="income-desc">–ü–æ –¥–æ—Ö–æ–¥—É (–º–∞–∫—Å)</option>
              <option value="income-asc">–ü–æ –¥–æ—Ö–æ–¥—É (–º–∏–Ω)</option>
              <option value="level-desc">–ü–æ —É—Ä–æ–≤–Ω—é (–º–∞–∫—Å)</option>
              <option value="level-asc">–ü–æ —É—Ä–æ–≤–Ω—é (–º–∏–Ω)</option>
              <option value="quality-desc">–ü–æ –∫–∞—á–µ—Å—Ç–≤—É (–º–∞–∫—Å)</option>
              <option value="quality-asc">–ü–æ –∫–∞—á–µ—Å—Ç–≤—É (–º–∏–Ω)</option>
              <option value="expires-asc">–ü–æ —Å—Ä–æ–∫—É (–º–∏–Ω)</option>
              <option value="expires-desc">–ü–æ —Å—Ä–æ–∫—É (–º–∞–∫—Å)</option>
            </select>
          </div>
        </div>
      </div>
      
      <div className="drops-page__content">
        {filteredAndSortedDrops.length > 0 ? (
          filteredAndSortedDrops.map(drop => (
            <DropCard 
              key={drop.id} 
              drop={drop} 
              onUpgrade={upgradeDropLevel}
              onRenew={renewDrop}
              onRelease={handleConfirmRelease}
            />
          ))
        ) : (
          <div className="drops-page__empty">
            <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –¥—Ä–æ–ø–æ–≤</p>
            <Button 
              variant="primary"
              onClick={handleInviteFriends}
            >
              –ü—Ä–∏–≥–ª–∞—Å–∏—Ç—å –¥—Ä—É–∑–µ–π
            </Button>
          </div>
        )}
      </div>
      
      <BottomNav />
    </div>
  );
};

export default DropsPage;
