import React, { useState, useEffect } from 'react';
import './Drops.scss';
import { Drop, DropQuality, DropsStats, DropCardProps } from '../../types/drop';
import DropCard from '../../components/Drops/DropCard';
import Button from '../../components/common/Button';

// Мок-данные дропов, соответствующие интерфейсу Drop из types/drop.ts
const mockDrops: Drop[] = [
  {
    id: '1',
    name: 'Иван Петров',
    quality: DropQuality.COMMON,
    level: 1,
    income: 10,
    isActive: true,
    expiresAt: new Date(Date.now() + 1000 * 60 * 60 * 24 * 3), // 3 дня
    upgradePrice: 500,
    renewPrice: 200
  },
  {
    id: '2',
    name: 'Анна Сидорова',
    quality: DropQuality.RARE,
    level: 2,
    avatarUrl: 'https://i.pravatar.cc/150?img=2',
    income: 50,
    isActive: true,
    expiresAt: new Date(Date.now() + 1000 * 60 * 60 * 24 * 5), // 5 дней
    upgradePrice: 1500,
    renewPrice: 500
  },
  {
    id: '3',
    name: 'Михаил Иванов',
    quality: DropQuality.EPIC,
    level: 3,
    avatarUrl: 'https://i.pravatar.cc/150?img=3',
    income: 180,
    isActive: true,
    expiresAt: new Date(Date.now() + 1000 * 60 * 60 * 24 * 2), // 2 дня
    upgradePrice: 3000,
    renewPrice: 1200
  },
  {
    id: '4',
    name: 'Елена Смирнова',
    quality: DropQuality.LEGENDARY,
    level: 4,
    avatarUrl: 'https://i.pravatar.cc/150?img=4',
    income: 600,
    isActive: false, // неактивный дроп
    expiresAt: new Date(Date.now() - 1000 * 60 * 60), // истек час назад
    upgradePrice: 6000,
    renewPrice: 3000
  }
];

const mockStats: DropsStats = {
  totalDrops: 4,
  activeDrops: 3,
  lostDrops24h: 1,
  totalIncome: 840 // доход в час со всех активных дропов
};

const DropsPage: React.FC = () => {
  const [drops, setDrops] = useState<Drop[]>(mockDrops);
  const [stats, setStats] = useState<DropsStats>(mockStats);
  const [balance, setBalance] = useState<number>(5000);
  const [filterActive, setFilterActive] = useState<boolean | null>(null);
  const [sortBy, setSortBy] = useState<string>('income');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  
  // Фильтрация и сортировка дропов
  const filteredAndSortedDrops = [...drops]
    .filter(drop => {
      if (filterActive === null) return true;
      return drop.isActive === filterActive;
    })
    .sort((a, b) => {
      let comparison = 0;
      
      switch (sortBy) {
        case 'income':
          comparison = a.income - b.income;
          break;
        case 'level':
          comparison = a.level - b.level;
          break;
        case 'quality':
          const qualityOrder = {
            [DropQuality.COMMON]: 0,
            [DropQuality.RARE]: 1,
            [DropQuality.EPIC]: 2,
            [DropQuality.LEGENDARY]: 3
          };
          comparison = qualityOrder[a.quality] - qualityOrder[b.quality];
          break;
        case 'expires':
          const aTime = a.expiresAt ? a.expiresAt.getTime() : Infinity;
          const bTime = b.expiresAt ? b.expiresAt.getTime() : Infinity;
          comparison = aTime - bTime;
          break;
        default:
          comparison = 0;
      }
      
      return sortOrder === 'asc' ? comparison : -comparison;
    });

  // Обработчики действий с дропами
  const handleUpgradeDrop = (dropId: string) => {
    const dropToUpgrade = drops.find(d => d.id === dropId);
    if (!dropToUpgrade || balance < dropToUpgrade.upgradePrice) return;
    
    setDrops(prevDrops => prevDrops.map(drop => {
      if (drop.id === dropId) {
        const newLevel = drop.level + 1;
        // Увеличиваем доход в соответствии с новым уровнем
        const newIncome = Math.floor(drop.income * 1.5);
        // Увеличиваем стоимость улучшения
        const newUpgradePrice = Math.floor(drop.upgradePrice * 1.8);
        
        return {
          ...drop,
          level: newLevel,
          income: newIncome,
          upgradePrice: newUpgradePrice
        };
      }
      return drop;
    }));
    
    // Списываем стоимость улучшения
    setBalance(prev => prev - dropToUpgrade.upgradePrice);
    
    // Обновляем статистику дохода
    updateStats();
  };
  
  const handleRenewDrop = (dropId: string) => {
    const dropToRenew = drops.find(d => d.id === dropId);
    if (!dropToRenew || balance < dropToRenew.renewPrice) return;
    
    setDrops(prevDrops => prevDrops.map(drop => {
      if (drop.id === dropId) {
        // Продлеваем на 7 дней
        const newExpiresAt = new Date();
        newExpiresAt.setDate(newExpiresAt.getDate() + 7);
        
        return {
          ...drop,
          isActive: true,
          expiresAt: newExpiresAt
        };
      }
      return drop;
    }));
    
    // Списываем стоимость продления
    setBalance(prev => prev - dropToRenew.renewPrice);
    
    // Обновляем статистику
    updateStats();
  };
  
  const handleReleaseDrop = (dropId: string) => {
    if (!window.confirm('Вы уверены, что хотите отпустить этот дроп? Это действие необратимо.')) {
      return;
    }
    
    setDrops(prevDrops => prevDrops.filter(drop => drop.id !== dropId));
    
    // Обновляем статистику
    updateStats();
  };
  
  // Адаптеры для передачи в DropCard
  const handleUpgradeAdapter = (drop: Drop) => {
    handleUpgradeDrop(drop.id);
  };
  
  const handleRenewAdapter = (drop: Drop) => {
    handleRenewDrop(drop.id);
  };
  
  const handleReleaseAdapter = (drop: Drop) => {
    handleReleaseDrop(drop.id);
  };
  
  // Групповые операции с дропами
  const handleMassUpgrade = () => {
    const activeDrops = drops.filter(drop => drop.isActive);
    const totalCost = activeDrops.reduce((sum, drop) => sum + drop.upgradePrice, 0);
    
    if (balance < totalCost) {
      alert(`Недостаточно средств для улучшения всех дропов. Требуется: ${totalCost} 💰`);
      return;
    }
    
    if (window.confirm(`Улучшить все активные дропы? Стоимость: ${totalCost} 💰`)) {
      // Улучшаем все активные дропы
      activeDrops.forEach(drop => handleUpgradeDrop(drop.id));
    }
  };
  
  const handleMassRenew = () => {
    const inactiveDrops = drops.filter(drop => !drop.isActive);
    const totalCost = inactiveDrops.reduce((sum, drop) => sum + drop.renewPrice, 0);
    
    if (balance < totalCost) {
      alert(`Недостаточно средств для продления всех дропов. Требуется: ${totalCost} 💰`);
      return;
    }
    
    if (window.confirm(`Продлить все неактивные дропы? Стоимость: ${totalCost} 💰`)) {
      // Продлеваем все неактивные дропы
      inactiveDrops.forEach(drop => handleRenewDrop(drop.id));
    }
  };
  
  // Функция обновления статистики
  const updateStats = () => {
    const activeDrops = drops.filter(drop => drop.isActive);
    const totalIncome = activeDrops.reduce((sum, drop) => sum + drop.income, 0);
    
    setStats({
      ...stats,
      totalDrops: drops.length,
      activeDrops: activeDrops.length,
      totalIncome
    });
  };
  
  // Эффект для инициализации и обновления данных
  useEffect(() => {
    // В реальном приложении здесь будет запрос к API для получения дропов
    
    // Обновляем статистику при изменении списка дропов
    updateStats();
  }, [drops]);
  
  // Обработчики изменения фильтров и сортировки
  const handleFilterChange = (value: boolean | null) => {
    setFilterActive(value);
  };
  
  const handleSortChange = (field: string) => {
    if (sortBy === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortBy(field);
      setSortOrder('desc');
    }
  };
  
  return (
    <div className="drops-page">
      <div className="drops-page__header">
        <h1 className="drops-page__title">Мои дропы</h1>
        <div className="drops-page__balance">
          Баланс: <span className="drops-page__balance-value">{balance} 💰</span>
        </div>
      </div>
      
      <div className="drops-page__stats">
        <div className="stats-card">
          <div className="stats-card__item">
            <div className="stats-card__label">Всего дропов</div>
            <div className="stats-card__value">{stats.totalDrops}</div>
          </div>
          <div className="stats-card__item">
            <div className="stats-card__label">Активные</div>
            <div className="stats-card__value">{stats.activeDrops}</div>
          </div>
          <div className="stats-card__item">
            <div className="stats-card__label">Потеряно за 24ч</div>
            <div className="stats-card__value">{stats.lostDrops24h}</div>
          </div>
          <div className="stats-card__item">
            <div className="stats-card__label">Общий доход</div>
            <div className="stats-card__value stats-card__value--income">{stats.totalIncome} 💰/час</div>
          </div>
        </div>
      </div>
      
      <div className="drops-page__actions">
        <div className="drops-page__mass-actions">
          <Button 
            variant="primary" 
            onClick={handleMassUpgrade}
            disabled={!drops.some(drop => drop.isActive)}
          >
            Улучшить все
          </Button>
          <Button 
            variant="secondary" 
            onClick={handleMassRenew}
            disabled={!drops.some(drop => !drop.isActive)}
          >
            Продлить все
          </Button>
        </div>
        
        <div className="drops-page__filters">
          <div className="drops-page__filter-group">
            <span className="drops-page__filter-label">Статус:</span>
            <Button 
              variant={filterActive === null ? "primary" : "outline"} 
              size="small" 
              onClick={() => handleFilterChange(null)}
            >
              Все
            </Button>
            <Button 
              variant={filterActive === true ? "primary" : "outline"} 
              size="small" 
              onClick={() => handleFilterChange(true)}
            >
              Активные
            </Button>
            <Button 
              variant={filterActive === false ? "primary" : "outline"} 
              size="small" 
              onClick={() => handleFilterChange(false)}
            >
              Неактивные
            </Button>
          </div>
          
          <div className="drops-page__filter-group">
            <span className="drops-page__filter-label">Сортировка:</span>
            <select 
              className="drops-page__sort-select" 
              value={`${sortBy}-${sortOrder}`}
              onChange={(e) => {
                const [field, order] = e.target.value.split('-');
                setSortBy(field);
                setSortOrder(order as 'asc' | 'desc');
              }}
            >
              <option value="income-desc">По доходу (макс)</option>
              <option value="income-asc">По доходу (мин)</option>
              <option value="level-desc">По уровню (макс)</option>
              <option value="level-asc">По уровню (мин)</option>
              <option value="quality-desc">По качеству (макс)</option>
              <option value="quality-asc">По качеству (мин)</option>
              <option value="expires-asc">По сроку (мин)</option>
              <option value="expires-desc">По сроку (макс)</option>
            </select>
          </div>
        </div>
      </div>
      
      <div className="drops-page__content">
        {filteredAndSortedDrops.length > 0 ? (
          filteredAndSortedDrops.map(drop => (
            <DropCard 
              key={drop.id} 
              drop={drop} 
              onUpgrade={handleUpgradeAdapter}
              onRenew={handleRenewAdapter}
              onRelease={handleReleaseAdapter}
            />
          ))
        ) : (
          <div className="drops-page__empty">
            <p>У вас пока нет дропов</p>
            <Button 
              variant="primary"
              onClick={() => alert('Поделитесь ссылкой на игру с друзьями, чтобы получить дропы!')}
            >
              Пригласить друзей
            </Button>
          </div>
        )}
      </div>
    </div>
  );
};

export default DropsPage;
