export type DropQuality = 'common' | 'rare' | 'epic' | 'legendary';

export interface Drop {
  drop_id: string;
  owner_id: string;
  invitee_id: string;
  invitee_name: string;
  quality: DropQuality;
  level: number;
  income_multiplier: number;
  income_per_hour: number;
  created_at: Date;
  avatar_url?: string;
  is_active: boolean;
  last_income_time: Date;
}

export interface DropUpgrade {
  cost: number;
  income_boost_percent: number;
}

// Мультипликаторы качества дропов
export const QUALITY_MULTIPLIERS = {
  common: 1.0,
  rare: 2.5, 
  epic: 6.0,
  legendary: 15.0
};

// Цвета для различных качеств дропов
export const QUALITY_COLORS = {
  common: '#78909C', // Серый
  rare: '#2196F3', // Синий
  epic: '#9C27B0', // Фиолетовый
  legendary: '#FFC107' // Золотой
};
