import React from 'react';
import './DropCard.scss';
import { Drop, QUALITY_COLORS } from '../../types/drops';
import Button from '../common/Button';

interface DropCardProps {
  drop: Drop;
  onUpgrade: (drop: Drop) => void;
  onRelease: (drop: Drop) => void;
  canUpgrade: boolean;
}

const DropCard: React.FC<DropCardProps> = ({ 
  drop, 
  onUpgrade, 
  onRelease,
  canUpgrade 
}) => {
  // Расчет времени с момента создания дропа
  const getTimeActive = (): string => {
    const now = new Date();
    const created = new Date(drop.created_at);
    const diffInMs = now.getTime() - created.getTime();
    const diffInDays = Math.floor(diffInMs / (1000 * 60 * 60 * 24));
    const diffInHours = Math.floor((diffInMs % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    
    if (diffInDays > 0) {
      return `${diffInDays}д ${diffInHours}ч`;
    } else {
      return `${diffInHours}ч`;
    }
  };

  // Получение цвета в зависимости от качества дропа
  const qualityColor = QUALITY_COLORS[drop.quality];

  return (
    <div className={`drop-card ${drop.quality} ${!drop.is_active ? 'inactive' : ''}`}>
      <div className="drop-card__avatar" style={{borderColor: qualityColor}}>
        {drop.avatar_url ? (
          <img src={drop.avatar_url} alt="аватар" />
        ) : (
          <div className="drop-card__avatar-placeholder">
            {drop.invitee_name.charAt(0).toUpperCase()}
          </div>
        )}
        <div className="drop-card__level">Ур.{drop.level}</div>
      </div>

      <div className="drop-card__info">
        <div className="drop-card__header">
          <h3 className="drop-card__name">{drop.invitee_name}</h3>
          <span className="drop-card__quality" style={{backgroundColor: qualityColor}}>
            {drop.quality === 'common' && 'Обычный'}
            {drop.quality === 'rare' && 'Редкий'}
            {drop.quality === 'epic' && 'Эпический'}
            {drop.quality === 'legendary' && 'Легендарный'}
          </span>
        </div>
        
        <div className="drop-card__details">
          <div className="drop-card__detail">
            <span className="drop-card__detail-label">Доход:</span>
            <span className="drop-card__detail-value drop-card__income">
              {drop.income_per_hour}/час
            </span>
          </div>
          
          <div className="drop-card__detail">
            <span className="drop-card__detail-label">Активен:</span>
            <span className="drop-card__detail-value">
              {getTimeActive()}
            </span>
          </div>
        </div>
      </div>
      
      <div className="drop-card__actions">
        <Button 
          variant="primary" 
          size="small" 
          onClick={() => onUpgrade(drop)}
          disabled={!canUpgrade}
        >
          Улучшить
        </Button>
        <Button 
          variant="outline" 
          size="small" 
          onClick={() => onRelease(drop)}
          className="drop-card__release-button"
        >
          Отпустить
        </Button>
      </div>
    </div>
  );
};

export default DropCard;
