import React from 'react';
import './DropCard.scss';
import { DropCardProps, DROP_CONSTANTS } from '../../types/drop';

const DropCard: React.FC<DropCardProps> = ({ 
  drop, 
  onUpgrade, 
  onRelease, 
  onExtraPayment,
  isSelected = false,
  isSelectable = false,
  onSelect
}) => {
  // Проверка активности дропа (активен, если был онлайн в течение последних 24 часов)
  const isActive = (): boolean => {
    const now = new Date();
    const lastSeen = new Date(drop.lastSeen);
    const diffHours = Math.abs(now.getTime() - lastSeen.getTime()) / 36e5; // 36e5 = 60*60*1000 (мс в часе)
    return diffHours < 24;
  };

  // Форматирование дохода
  const formatIncome = (amount: number): string => {
    return new Intl.NumberFormat('ru-RU', {
      style: 'currency',
      currency: 'RUB',
      maximumFractionDigits: 0
    }).format(amount);
  };

  // Расчет стоимости улучшения
  const calculateUpgradeCost = (): number => {
    return Math.floor(DROP_CONSTANTS.BASE_UPGRADE_COST * 
      Math.pow(DROP_CONSTANTS.UPGRADE_MULTIPLIER, drop.level));
  };

  // Получение класса качества для стилизации
  const getQualityClass = (): string => {
    switch (drop.quality) {
      case 'common':
        return 'drop-card--common';
      case 'rare':
        return 'drop-card--rare';
      case 'epic':
        return 'drop-card--epic';
      case 'legendary':
        return 'drop-card--legendary';
      default:
        return 'drop-card--common';
    }
  };

  // Получение текста качества
  const getQualityText = (): string => {
    switch (drop.quality) {
      case 'common':
        return 'Обычный';
      case 'rare':
        return 'Редкий';
      case 'epic':
        return 'Эпический';
      case 'legendary':
        return 'Легендарный';
      default:
        return 'Обычный';
    }
  };

  const active = isActive();
  const qualityClass = getQualityClass();
  const upgradeCost = calculateUpgradeCost();
  
  // Обработчик клика для предотвращения всплытия событий
  const handleActionClick = (e: React.MouseEvent, callback: (id: string) => void) => {
    e.stopPropagation();
    callback(drop.id);
  };
  
  return (
    <div 
      className={`drop-card ${qualityClass} ${!active ? 'drop-card--inactive' : ''} ${isSelected ? 'drop-card--selected' : ''}`}
      onClick={isSelectable && onSelect ? () => onSelect(drop.id) : undefined}
    >
      <div className="drop-card__header">
        <div className="drop-card__avatar">
          {drop.avatar ? (
            <img src={drop.avatar} alt={drop.name} />
          ) : (
            <div className="drop-card__avatar-placeholder">
              {drop.name.charAt(0).toUpperCase()}
            </div>
          )}
        </div>
        <div className="drop-card__info">
          <h3 className="drop-card__name">{drop.name}</h3>
          <span className="drop-card__quality">{getQualityText()}</span>
        </div>
      </div>
      
      <div className="drop-card__body">
        <div className="drop-card__stats-row">
          <div className="drop-card__stat">
            <span className="drop-card__stat-label">Уровень:</span>
            <span className="drop-card__stat-value">{drop.level}</span>
          </div>
          <div className="drop-card__stat">
            <span className="drop-card__stat-label">Доход в час:</span>
            <span className="drop-card__stat-value">{formatIncome(drop.incomePerHour)}</span>
          </div>
          <div className="drop-card__stat">
            <span className="drop-card__stat-label">Улучшение:</span>
            <span className="drop-card__stat-value">{formatIncome(upgradeCost)}</span>
          </div>
        </div>
      </div>
      
      <div className="drop-card__actions">
        <button 
          className="drop-card__action drop-card__action--upgrade"
          onClick={(e) => handleActionClick(e, onUpgrade)}
        >
          Улучшить
        </button>
        <button 
          className="drop-card__action drop-card__action--pay"
          onClick={(e) => handleActionClick(e, onExtraPayment)}
          disabled={!active}
        >
          Доплатить
        </button>
        <button 
          className="drop-card__action drop-card__action--release"
          onClick={(e) => handleActionClick(e, onRelease)}
        >
          Отпустить
        </button>
      </div>
    </div>
  );
};

export default DropCard;
