import React from 'react';
import './Icon.scss';

interface IconProps {
  name: string;
  size?: 'sm' | 'md' | 'lg' | 'xl';
  color?: string;
  className?: string;
}

// –ö–æ–º–ø–æ–Ω–µ–Ω—Ç –∏–∫–æ–Ω–æ–∫ —Å —ç–º–æ–¥–∑–∏ –∏–ª–∏ svg –∏–∫–æ–Ω–∫–∞–º–∏
const Icon: React.FC<IconProps> = ({ 
  name, 
  size = 'md', 
  color,
  className = ''
}) => {
  // –ë–∞–∑–æ–≤—ã–µ —ç–º–æ–¥–∑–∏ –∏–∫–æ–Ω–∫–∏ –¥–ª—è —Ä–∞–∑–Ω—ã—Ö —Ä–∞–∑–¥–µ–ª–æ–≤
  const emojiIcons: {[key: string]: string} = {
    'business': 'üè¢',
    'drops': 'üë•',
    'darknet': 'üï∏Ô∏è',
    'shop': 'üõí',
    'profile': 'üë§',
    'rating': 'üèÜ',
    'money': 'üí∞',
    'upgrade': '‚¨ÜÔ∏è',
    'settings': '‚öôÔ∏è',
    'close': '‚úñÔ∏è',
    'add': '‚ûï',
    'success': '‚úÖ',
    'error': '‚ùå',
    'warning': '‚ö†Ô∏è',
    'info': '‚ÑπÔ∏è'
  };

  return (
    <span 
      className={`icon icon--${size} ${className}`} 
      style={color ? { color } : {}}
    >
      {emojiIcons[name] || name}
    </span>
  );
};

export default Icon;
