import React, { useState, useEffect } from 'react';
import './Drops.scss';
import { Drop, DropQuality, DropStats } from '../../types/drop';
import DropCard from '../../components/Drops/DropCard/index';
import Button from '../../components/common/Button';
import Icon from '../../components/common/Icon';

// Временные данные для демонстрации
const mockDrops: Drop[] = [
  {
    id: '1',
    userId: 'user1',
    username: 'Пользователь 1',
    quality: DropQuality.COMMON,
    level: 1,
    baseIncome: 100,
    lastCollected: new Date(Date.now() - 30 * 60 * 60 * 1000), // 30 часов назад
    createdAt: new Date(),
    updatedAt: new Date()
  },
  {
    id: '2',
    userId: 'user2',
    username: 'Пользователь 2',
    quality: DropQuality.RARE,
    level: 2,
    baseIncome: 150,
    lastCollected: new Date(Date.now() - 10 * 60 * 60 * 1000), // 10 часов назад
    createdAt: new Date(),
    updatedAt: new Date()
  },
  {
    id: '3',
    userId: 'user3',
    username: 'Пользователь 3',
    quality: DropQuality.EPIC,
    level: 3,
    baseIncome: 200,
    lastCollected: new Date(Date.now() - 30 * 60 * 60 * 1000), // 30 часов назад
    createdAt: new Date(),
    updatedAt: new Date()
  }
];

const mockStats: DropStats = {
  totalDrops: 15,
  lostDrops24h: 3,
  totalIncome: 5000,
  incomeByQuality: {
    [DropQuality.COMMON]: 1500,
    [DropQuality.RARE]: 2000,
    [DropQuality.EPIC]: 1000,
    [DropQuality.LEGENDARY]: 500
  }
};

const DropsPage: React.FC = () => {
  const [drops, setDrops] = useState<Drop[]>([]);
  const [stats, setStats] = useState<DropStats | null>(null);
  const [balance, setBalance] = useState<number>(1000);
  const [loading, setLoading] = useState<boolean>(true);

  // Загружаем данные при монтировании
  useEffect(() => {
    // Здесь будет API запрос в будущем
    setTimeout(() => {
      setDrops(mockDrops);
      setStats(mockStats);
      setLoading(false);
    }, 1000);
  }, []);

  // Адаптеры для функций обработчиков
  const handleUpgradeDrop = (drop: Drop) => {
    handleUpgrade(drop.id);
  };

  const handleReleaseDrop = (drop: Drop) => {
    handleRelease(drop.id);
  };

  const handleCollectDrop = (drop: Drop) => {
    handleCollect(drop.id);
  };

  // Функция для улучшения уровня дропа
  const handleUpgrade = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    setDrops(prevDrops => 
      prevDrops.map(d => {
        if (d.id === dropId) {
          // Увеличиваем уровень дропа
          return { ...d, level: d.level + 1, updatedAt: new Date() };
        }
        return d;
      })
    );
    
    // Уменьшаем баланс (в реальном приложении стоимость будет получена с сервера)
    setBalance(prevBalance => prevBalance - 100);
  };

  // Функция для освобождения дропа
  const handleRelease = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    setDrops(prevDrops => prevDrops.filter(d => d.id !== dropId));
    
    // Обновляем статистику
    if (stats) {
      setStats({
        ...stats,
        totalDrops: stats.totalDrops - 1
      });
    }
  };

  // Функция для сбора дохода с дропа
  const handleCollect = (dropId: string) => {
    // В реальном приложении здесь будет API запрос
    setDrops(prevDrops => 
      prevDrops.map(d => {
        if (d.id === dropId) {
          // Обновляем время последнего сбора
          return { ...d, lastCollected: new Date() };
        }
        return d;
      })
    );
    
    // Увеличиваем баланс (в реальном приложении сумма будет получена с сервера)
    const collectableDrop = drops.find(d => d.id === dropId);
    if (collectableDrop) {
      const incomeAmount = 100; // Пример суммы (в реальном приложении будет расчет)
      setBalance(prevBalance => prevBalance + incomeAmount);
    }
  };

  // Ищем новый дроп
  const handleFindDrop = () => {
    // В реальном приложении здесь будет API запрос
    setLoading(true);
    
    setTimeout(() => {
      // Создаем новый случайный дроп
      const newDrop: Drop = {
        id: Math.random().toString(),
        userId: 'currentUser',
        username: 'Случайный пользователь',
        quality: Object.values(DropQuality)[Math.floor(Math.random() * 4)],
        level: 1,
        baseIncome: 100 + Math.floor(Math.random() * 100),
        lastCollected: new Date(),
        createdAt: new Date(),
        updatedAt: new Date()
      };
      
      setDrops(prevDrops => [...prevDrops, newDrop]);
      
      // Обновляем статистику
      if (stats) {
        setStats({
          ...stats,
          totalDrops: stats.totalDrops + 1
        });
      }
      
      setLoading(false);
    }, 1000);
  };

  return (
    <div className="drops-page">
      <header className="drops-page__header">
        <h1 className="drops-page__title">Мои дропы</h1>
        <div className="drops-page__balance">
          <span className="drops-page__balance-label">Баланс:</span>
          <span className="drops-page__balance-value">{balance.toFixed(2)} ₽</span>
        </div>
      </header>

      {stats && (
        <div className="drops-page__stats">
          <div className="drops-page__stat-item">
            <span className="drops-page__stat-label">Всего дропов:</span>
            <span className="drops-page__stat-value">{stats.totalDrops}</span>
          </div>
          <div className="drops-page__stat-item">
            <span className="drops-page__stat-label">Потеряно за 24ч:</span>
            <span className="drops-page__stat-value">{stats.lostDrops24h}</span>
          </div>
          <div className="drops-page__stat-item">
            <span className="drops-page__stat-label">Общий доход:</span>
            <span className="drops-page__stat-value">{stats.totalIncome.toFixed(2)} ₽</span>
          </div>
        </div>
      )}

      <div className="drops-page__actions">
        <Button 
          variant="accent" 
          onClick={handleFindDrop}
          disabled={loading}
        >
          <Icon name="drops" size="sm" /> Искать дроп
        </Button>
      </div>

      {loading ? (
        <div className="drops-page__loading">Загрузка...</div>
      ) : drops.length > 0 ? (
        <div className="drops-page__list">
          {drops.map(drop => (
            <DropCard
              key={drop.id}
              drop={drop}
              onUpgrade={(dropId) => handleUpgrade(dropId)}
              onRelease={(dropId) => handleRelease(dropId)}
              onCollect={(dropId) => handleCollect(dropId)}
            />
          ))}
        </div>
      ) : (
        <div className="drops-page__empty">
          <p>У вас пока нет дропов. Нажмите "Искать дроп", чтобы найти свой первый дроп!</p>
        </div>
      )}
    </div>
  );
};

export default DropsPage;
