import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Drops.scss';
import DropCard from '../../components/Drops/DropCard';
import Button from '../../components/common/Button';
import Loader from '../../components/common/Loader';
import { useGame } from '../../context/GameContext';
import BottomNav from '../../components/common/BottomNav';
import DropFilters, { DropSortOption } from '../../components/Drops/DropFilters';
import { DROP_CONSTANTS } from '../../types/drop';

const DropsPage: React.FC = () => {
  const navigate = useNavigate();
  const { drops, setDrops, balance, lostDrops24h, totalDropsCount, dropsIncomePerHour, addDrop } = useGame();
  
  const [loading, setLoading] = useState<boolean>(true);
  const [selectedDrops, setSelectedDrops] = useState<string[]>([]);
  const [isMultiSelectMode, setIsMultiSelectMode] = useState<boolean>(false);
  const [sortBy, setSortBy] = useState<DropSortOption>('level_desc');

  // Сбрасываем выбор при выходе из режима выбора
  useEffect(() => {
    if (!isMultiSelectMode) {
      setSelectedDrops([]);
    }
  }, [isMultiSelectMode]);

  // Имитация загрузки данных и добавление тестовых дропов если их нет
  useEffect(() => {
    // Добавляем тестовые дропы, если их нет
    if (drops.length === 0) {
      // Тестовые дропы
      const testDrops = [
        {
          id: '1',
          userId: '123456789',
          name: 'Алексей',
          avatar: 'https://randomuser.me/api/portraits/men/32.jpg',
          quality: 'common',
          level: 1,
          incomePerHour: 10,
          incomeMultiplier: 1.0,
          lastSeen: new Date(Date.now() - 1000 * 60 * 10).toISOString(), // 10 минут назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 2).toISOString() // 2 дня назад
        },
        {
          id: '2',
          userId: '987654321',
          name: 'Мария',
          avatar: 'https://randomuser.me/api/portraits/women/43.jpg',
          quality: 'rare',
          level: 2,
          incomePerHour: 25,
          incomeMultiplier: 2.5,
          lastSeen: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString(), // 2 часа назад
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 5).toISOString() // 5 дней назад
        },
        {
          id: '3',
          userId: '555555555',
          name: 'Иван',
          avatar: 'https://randomuser.me/api/portraits/men/67.jpg',
          quality: 'epic',
          level: 3,
          incomePerHour: 60,
          incomeMultiplier: 6.0,
          lastSeen: new Date().toISOString(), // сейчас
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 10).toISOString() // 10 дней назад
        },
        {
          id: '4',
          userId: '444444444',
          name: 'Екатерина',
          avatar: 'https://randomuser.me/api/portraits/women/22.jpg',
          quality: 'legendary',
          level: 4,
          incomePerHour: 150,
          incomeMultiplier: 15.0,
          lastSeen: new Date(Date.now() - 1000 * 60 * 60 * 30).toISOString(), // 30 часов назад (неактивный)
          createdAt: new Date(Date.now() - 1000 * 60 * 60 * 24 * 15).toISOString() // 15 дней назад
        }
      ];
      
      // Добавляем каждый дроп
      testDrops.forEach(drop => {
        addDrop(drop);
      });
    }

    // Имитируем задержку загрузки
    const timer = setTimeout(() => {
      setLoading(false);
    }, 800);
    
    return () => clearTimeout(timer);
  }, [drops.length, addDrop]);

  // Расчет стоимости улучшения для дропов
  const calculateUpgradeCost = (level: number): number => {
    return Math.floor(DROP_CONSTANTS.BASE_UPGRADE_COST * 
      Math.pow(DROP_CONSTANTS.UPGRADE_MULTIPLIER, level));
  };

  // Сортировка дропов в зависимости от выбранного критерия
  const sortedDrops = [...drops].sort((a, b) => {
    switch(sortBy) {
      case 'level_desc':
        return b.level - a.level;
      case 'level_asc':
        return a.level - b.level;
      case 'income':
        return b.incomePerHour - a.incomePerHour;
      case 'upgrade_cost':
        return calculateUpgradeCost(a.level) - calculateUpgradeCost(b.level);
      default:
        return 0;
    }
  });

  const handleUpgrade = (dropId: string) => {
    if (isMultiSelectMode) return; // Блокируем действия с дропами в режиме выбора
    
    // В реальном приложении здесь будет API запрос
    console.log(`Улучшение дропа ${dropId}`);
  };

  const handleRelease = (dropId: string) => {
    if (isMultiSelectMode) return; // Блокируем действия с дропами в режиме выбора
    
    // В реальном приложении здесь будет API запрос
    console.log(`Отпускание дропа ${dropId}`);
    setDrops(drops.filter(drop => drop.id !== dropId));
    // Удаляем дроп из выбранных, если он был выбран
    if (selectedDrops.includes(dropId)) {
      setSelectedDrops(prevSelected => prevSelected.filter(id => id !== dropId));
    }
  };

  const handleExtraPayment = (dropId: string) => {
    if (isMultiSelectMode) return; // Блокируем действия с дропами в режиме выбора
    
    // В реальном приложении здесь будет API запрос
    console.log(`Доплата за дроп ${dropId}`);
  };

  // Новый обработчик для управления выбором дропов
  const handleDropCardClick = (e: React.MouseEvent, dropId: string) => {
    // Только если мы в режиме выбора
    if (!isMultiSelectMode) return;
    
    // Проверяем, что клик был по обертке, а не по кнопкам внутри карточки
    const target = e.target as HTMLElement;
    if (target.closest('.drop-card__action')) {
      return; // Игнорируем клики по кнопкам
    }
    
    // Переключаем выбор
    setSelectedDrops(prevSelected => {
      if (prevSelected.includes(dropId)) {
        return prevSelected.filter(id => id !== dropId);
      } else {
        return [...prevSelected, dropId];
      }
    });
  };

  const toggleMultiSelectMode = () => {
    setIsMultiSelectMode(prevMode => !prevMode);
  };

  const handleBulkAction = (action: 'upgrade' | 'release' | 'payExtra') => {
    if (selectedDrops.length === 0) return; // Предотвращаем действия, если ничего не выбрано
    
    // В реальном приложении здесь будет API запрос
    console.log(`Действие ${action} для дропов:`, selectedDrops);
    
    if (action === 'release') {
      setDrops(drops.filter(drop => !selectedDrops.includes(drop.id)));
    }
    
    setSelectedDrops([]);
    setIsMultiSelectMode(false);
  };

  const selectAll = () => {
    if (selectedDrops.length === drops.length) {
      setSelectedDrops([]);
    } else {
      setSelectedDrops(drops.map(drop => drop.id));
    }
  };

  return (
    <div className="drops-page">
      <div className="drops-header">
        <h1>Мои Дропы</h1>
        <div className="balance">
          Баланс: <span>{new Intl.NumberFormat('ru-RU').format(balance)}</span> ₽
        </div>
      </div>

      <div className="drops-stats">
        <div className="stat-item">
          <span className="stat-label">Всего дропов:</span>
          <span className="stat-value">{totalDropsCount}</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Доход/час:</span>
          <span className="stat-value">{new Intl.NumberFormat('ru-RU').format(dropsIncomePerHour)} ₽</span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Активных:</span>
          <span className="stat-value">
            {drops.filter(drop => {
              const lastSeen = new Date(drop.lastSeen);
              const now = new Date();
              return (now.getTime() - lastSeen.getTime()) / (1000 * 60 * 60) < 24;
            }).length}
          </span>
        </div>
        <div className="stat-item">
          <span className="stat-label">Потеряно за 24ч:</span>
          <span className="stat-value">{lostDrops24h}</span>
        </div>
      </div>

      <DropFilters 
        sortBy={sortBy}
        onChangeSortBy={setSortBy}
      />

      <div className="drops-controls">
        <Button 
          onClick={toggleMultiSelectMode} 
          className={isMultiSelectMode ? 'active' : ''}
        >
          {isMultiSelectMode ? 'Отменить выбор' : 'Выбрать несколько'}
        </Button>
        
        {isMultiSelectMode && (
          <div className="multi-select-panel">
            {selectedDrops.length > 0 ? (
              <div className="vertical-bulk-actions">
                <div className="selected-count">Выбрано: {selectedDrops.length} из {drops.length}</div>
                <Button onClick={() => handleBulkAction('upgrade')} className="bulk-action-button">
                  Улучшить выбранные
                </Button>
                <Button onClick={() => handleBulkAction('payExtra')} className="bulk-action-button">
                  Доплатить выбранным
                </Button>
                <Button onClick={() => handleBulkAction('release')} className="bulk-action-button release-button">
                  Отпустить выбранные
                </Button>
              </div>
            ) : (
              <div className="no-selection-message">Выберите дропы для массовых действий</div>
            )}
            <Button onClick={selectAll} className="select-all-button">
              {selectedDrops.length === drops.length ? 'Снять выделение' : 'Выбрать все'}
            </Button>
          </div>
        )}
      </div>

      {loading ? (
        <div className="drops-loading">
          <Loader />
        </div>
      ) : (
        <div className="drops-grid">
          {sortedDrops.length > 0 ? (
            sortedDrops.map(drop => (
              <div 
                key={drop.id} 
                className={`drop-card-wrapper ${isMultiSelectMode ? 'selectable' : ''} ${selectedDrops.includes(drop.id) ? 'selected' : ''}`}
                onClick={(e) => handleDropCardClick(e, drop.id)}
              >
                <DropCard
                  drop={drop as any} // Временное решение для типизации
                  onUpgrade={handleUpgrade}
                  onRelease={handleRelease}
                  onExtraPayment={handleExtraPayment}
                  isSelected={selectedDrops.includes(drop.id)}
                />
              </div>
            ))
          ) : (
            <div className="no-drops">
              <p>У вас пока нет дропов</p>
              <Button onClick={() => navigate('/invite')}>Пригласить друзей</Button>
            </div>
          )}
        </div>
      )}
      
      <BottomNav />
    </div>
  );
};

export default DropsPage;
